import { v4 as uuid } from "uuid";
export function NewRequest(method, params) {
    return { jsonrpc: "2.0", id: uuid(), method, params };
}
export function NewSuccessResponse(id, result) {
    return { jsonrpc: "2.0", id, result };
}
export function NewErrorResponse(id, error) {
    return { jsonrpc: "2.0", id, error };
}
export class JsonRPCError extends Error {
    jsonrpc;
    constructor(reqID, code, message, data) {
        super(message);
        this.jsonrpc = NewErrorResponse(reqID, { code, message, data });
    }
}
